import React from 'react';
import './VideoCards.css';
import '../App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { Button } from './Button';
import './Button.css';

function VideoCards() {
  const [listOfVideos, setListOfVideos] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/videos").then((response) => {
      setListOfVideos(response.data);
    });
  }, []);

  function sliceArrIntoChunks(arr, chunkSize) {
    const res = [];
    for (let i = 0; i < arr.length; i += chunkSize) {
        const chunk = arr.slice(i, i + chunkSize);
        res.push(chunk);
    }
    return res;
  }

  const newVideosArr = sliceArrIntoChunks(listOfVideos, 3);
  console.log(newVideosArr[1][0].url)

  return (
    <div className='cards background'>
      <h1 className='gallery-title'>Gallery</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
            {newVideosArr.forEach( (arr) => arr.map((value, key) => {
              return (
                <ul className='cards__items'>
                <li className="video cards__item">
                  <div className="title">{value.title}</div>
                  <iframe className="url" width="396" height="300" src={value.url}></iframe>
                  <div className="description">{value.description}</div>
                </li>
                </ul>
              );
              }))
            }
        </div>
      </div>
      <Button className='btns' buttonStyle='btn--outline' buttonSize='btn--large'>See more here</Button>
    </div>
  );
}

export default VideoCards;
